contracts/deal/PaymentModule.sol.content:
// SPDX-License-Identifier: Apache-2.0

pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/structs/BitMaps.sol";
import "./interfaces/IPaymentModule.sol";
import "./interfaces/ICore.sol";
import "./interfaces/IWorkersModule.sol";
import "./base/Types.sol";
import "./base/ModuleBase.sol";

contract PaymentModuleState {
    // ----------------- Types -----------------
    struct ParticleResult {
        bool isValid;
        uint256 epoch;
        uint256 worketsCount;
        uint256 reward;
    }

    // ----------------- Events -----------------
    event DepositedToPaymentBalance(uint256 amount);
    event WithdrawnFromPaymentBalance(uint256 amount);

    event ParticleCommitted(bytes32 particleHash, uint256 epoch, uint256 workersCount, uint256 reward);

    event RewardWithdrawn(bytes32 patId, bytes32 particleHash, uint256 reward);

    // ----------------- Internal Vars -----------------
    uint256 internal _totalBalance;
    uint256 internal _lockedBalance;

    mapping(uint => uint) internal _goldenParticleCountByEpoch;
    mapping(bytes32 => ParticleResult) internal _particleResults;
    mapping(uint => mapping(uint => uint)) internal _workersByEpoch;
    mapping(bytes32 => BitMaps.BitMap) internal _paidWorkersByParticle;
}

contract PaymentModuleInternal is ModuleBase, PaymentModuleState {
    function _freeBalance() internal view returns (uint256) {
        return _totalBalance - _lockedBalance;
    }

    function _hasWorkerInEpoch(uint epoch, bytes32 patId) internal view returns (bool) {
        IWorkersModule workers = _core().workersModule();

        uint index = workers.getPAT(patId).index;
        uint256 bucket = index >> 8;
        uint256 mask = 1 << (index & 0xff);
        if (_workersByEpoch[epoch][bucket] & mask == 0) {
            return false;
        }

        return true;
    }
}

abstract contract PaymentModuleOwnable is PaymentModuleInternal, IPaymentModule {
    using BitMaps for BitMaps.BitMap;
    using SafeERC20 for IERC20;

    function depositToPaymentBalance(uint256 amount) external onlyOwner {
        IERC20 token = _core().configModule().paymentToken();
        token.safeTransferFrom(msg.sender, address(this), amount);
        _totalBalance += amount;

        emit DepositedToPaymentBalance(amount);
    }

    function withdrawFromPaymentBalance(uint256 amount) external onlyOwner {
        IERC20 token = _core().configModule().paymentToken();
        require(_freeBalance() >= amount, "Not enough free balance");

        _totalBalance -= amount;
        token.safeTransfer(msg.sender, amount);

        emit WithdrawnFromPaymentBalance(amount);
    }
}

contract PaymentModule is PaymentModuleOwnable {
    using BitMaps for BitMaps.BitMap;
    using SafeERC20 for IERC20;

    // ----------------- View -----------------
    function getRewardAmount(bytes32 particleHash, bytes32 patId) public view returns (uint) {
        ParticleResult storage particleResults = _particleResults[particleHash];

        require(particleResults.isValid, "Particle not valid");

        // TODO: a lot of calls :(
        uint currentEpoch = _core().configModule().globalConfig().epochManager().currentEpoch();
        uint particleEpoch = particleResults.epoch;

        require(currentEpoch - 1 > particleEpoch, "Particle not confirmed");

        if (!_hasWorkerInEpoch(particleEpoch, patId) || !_hasWorkerInEpoch(particleEpoch - 1, patId)) {
            return 0;
        }

        return particleResults.reward / particleResults.worketsCount;
    }

    function getPaymentBalance() public view returns (uint256) {
        return _totalBalance - _lockedBalance;
    }

    function getLockedBalance() external view returns (uint256) {
        return _lockedBalance;
    }

    // ----------------- Mutable -----------------
    function commitParticle(Particle calldata particle, bytes32[] memory patIds) external {
        // check particle
        bytes32 particleHash = keccak256(abi.encode(particle.air, particle.prevData, particle.params, particle.callResults));
        require(_particleResults[particleHash].epoch == 0, "Particle already exists");

        // load deal modules
        ICore core = _core();
        IConfigModule config = core.configModule();
        IWorkersModule workers = core.workersModule();

        // check patIds
        uint epoch = config.globalConfig().epochManager().currentEpoch();
        for (uint i = 0; i < patIds.length; i++) {
            uint index = workers.getPAT(patIds[i]).index;
            uint256 bucket = index >> 8;
            uint256 mask = 1 << (index & 0xff);
            _workersByEpoch[epoch][bucket] |= mask;
        }

        // calculate reward
        uint goldenParticleCountByEpoch = _goldenParticleCountByEpoch[epoch];
        uint pricePerEpoch = config.pricePerEpoch();
        uint totalReward = pricePerEpoch / (2 ** (goldenParticleCountByEpoch + 1));

        // save reward and particleResult
        _particleResults[particleHash] = ParticleResult({ isValid: true, epoch: epoch, worketsCount: patIds.length, reward: totalReward });
        _goldenParticleCountByEpoch[epoch] = ++goldenParticleCountByEpoch;

        // lock reward
        _totalBalance -= totalReward;
        _lockedBalance += totalReward;

        emit ParticleCommitted(particleHash, epoch, patIds.length, totalReward);
    }

    function withdrawReward(bytes32 patId, bytes32[] calldata particlesHashes) external {
        // load deal modules
        ICore core = _core();
        IConfigModule config = core.configModule();
        IWorkersModule workers = core.workersModule();

        uint index = workers.getPAT(patId).index;

        // calculate total reward
        uint totalReward;
        for (uint i = 0; i < particlesHashes.length; i++) {
            bytes32 particleHash = particlesHashes[i];
            BitMaps.BitMap storage paidWorkers = _paidWorkersByParticle[particleHash];

            require(!paidWorkers.get(index), "Already paid");

            uint256 reward = getRewardAmount(particleHash, patId);
            totalReward += reward;

            paidWorkers.set(index);

            emit RewardWithdrawn(patId, particleHash, reward);
        }

        _lockedBalance -= totalReward;
        config.paymentToken().safeTransfer(workers.getPAT(patId).owner, totalReward);
    }
}

contracts/deal/StatusModule.sol.content:
// SPDX-License-Identifier: Apache-2.0

pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./Core.sol";
import "./base/ModuleBase.sol";
import "./interfaces/IStatusModule.sol";
import "./base/Types.sol";

contract StatusModuleState {
    DealStatus internal _status_;
    uint256 internal _startWorkingEpoch_;
}

contract StatusModuleInternal is ModuleBase, StatusModuleState {
    // ----------------- Private -----------------

    function _onStartWorking() internal {
        _startWorkingEpoch_ = _core().configModule().globalConfig().epochManager().currentEpoch();
    }

    function _onEndWorking() internal {
        _startWorkingEpoch_ = 0;
    }
}

contract StatusModule is StatusModuleInternal, IStatusModule {
    // ----------------- View -----------------
    function status() external view returns (DealStatus) {
        return _status_;
    }

    function startWorkingEpoch() external view returns (uint256) {
        return _startWorkingEpoch_;
    }

    // ----------------- Mutable -----------------
    function changeStatus(DealStatus status_) external onlyModule(Module.Workers) {
        DealStatus oldStatus = _status_;

        if (oldStatus == status_) {
            return;
        }

        if (oldStatus != status_ && status_ == DealStatus.Working) {
            _onStartWorking();
        } else if (oldStatus != status_ && status_ == DealStatus.WaitingForWorkers) {
            _onEndWorking();
        }

        _status_ = status_;

        emit StatusChanged(status_);
    }
}

contracts/deal/WorkersModule.sol.content:
// SPDX-License-Identifier: Apache-2.0

pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/StorageSlot.sol";
import "./base/ModuleBase.sol";
import "../global/interfaces/IGlobalConfig.sol";
import "../utils/LinkedList.sol";
import "../utils/WithdrawRequests.sol";
import "./interfaces/IWorkersModule.sol";
import "./interfaces/IConfigModule.sol";
import "./interfaces/ICore.sol";
import "./interfaces/IStatusModule.sol";
import "./base/Types.sol";

contract WorkersModuleState {
    using LinkedList for LinkedList.Bytes32List;

    // ---- Structs ----

    struct OwnerInfo {
        uint256 patCount;
        LinkedList.Bytes32List patsIds;
    }

    // ---- Constants ----
    bytes32 internal constant _PAT_PREFIX = keccak256("fluence.pat");

    // ---- Events ----
    event PATCreated(bytes32 id, address owner);
    event PATRemoved(bytes32 id);

    event WorkerRegistred(bytes32 patId, bytes32 workerId);
    event WorkerUnregistred(bytes32 patId);

    event CollateralWithdrawn(address owner, uint256 amount);

    // ---- Storage ----
    uint256 internal _patCount;
    LinkedList.Bytes32List _freeIndexes;

    mapping(address => OwnerInfo) internal _ownersInfo;
    mapping(bytes32 => PAT) internal _patById;

    LinkedList.Bytes32List _patsIdsList;

    mapping(address => WithdrawRequests.Requests) internal _withdrawRequests;
}

contract WorkersModule is WorkersModuleState, ModuleBase, IWorkersModule {
    using LinkedList for LinkedList.Bytes32List;
    using WithdrawRequests for WithdrawRequests.Requests;
    using SafeERC20 for IERC20;

    // ---- Public view ----

    function getPAT(bytes32 id) external view returns (PAT memory) {
        return _patById[id];
    }

    function getPATCount() external view returns (uint256) {
        return _patCount;
    }

    function getPATs() public view returns (PAT[] memory) {
        PAT[] memory pats = new PAT[](_patCount);

        uint256 index = 0;
        bytes32 patId = _patsIdsList.first();
        while (patId != bytes32(0)) {
            pats[index] = _patById[patId];
            index++;

            patId = _patsIdsList.next(patId);
        }

        return pats;
    }

    function getUnlockedAmountBy(address owner, uint256 timestamp) external view returns (uint256) {
        IGlobalConfig globalConfig = _core().configModule().globalConfig();
        return _withdrawRequests[owner].getAmountBy(timestamp - globalConfig.withdrawTimeout());
    }

    // ---- Public Mutable ----
    function createPAT(address computeProvider, bytes32 peerId) external returns (bytes32) {
        ICore core = _core();

        // check params and limits
        IConfigModule config = core.configModule();

        uint256 globalPATCount = _patCount;
        require(globalPATCount < config.targetWorkers(), "Target workers reached");

        // transfer collateral
        uint256 requiredCollateral = config.requiredCollateral();
        config.fluenceToken().safeTransferFrom(msg.sender, address(this), requiredCollateral);

        // create PAT
        bytes32 id = keccak256(abi.encodePacked(_PAT_PREFIX, computeProvider, peerId));
        require(_patById[id].owner == address(0x00), "Id already used");

        uint256 patCountByOwner = _ownersInfo[computeProvider].patCount;

        _ownersInfo[computeProvider].patCount = ++patCountByOwner;
        _patCount = ++globalPATCount;

        uint index = uint(_freeIndexes.first());
        if (index == 0) {
            index = globalPATCount;
        }

        _patById[id] = PAT({
            id: id,
            peerId: peerId,
            index: index,
            workerId: bytes32(0),
            owner: computeProvider,
            collateral: requiredCollateral,
            created: block.number
        });

        _patsIdsList.push(id);

        // change status
        IStatusModule statusController = core.statusModule();
        {
            DealStatus status = statusController.status();
            if (status == DealStatus.WaitingForWorkers && globalPATCount >= config.minWorkers()) {
                status = DealStatus.Working;
                statusController.changeStatus(status);
            }
        }

        emit PATCreated(id, computeProvider);

        return id;
    }

    function setWorker(bytes32 patId, bytes32 workerId) external {
        PAT storage pat = _patById[patId];

        if (pat.workerId != bytes32(0)) {
            emit WorkerUnregistred(patId);
        }

        pat.workerId = workerId;

        emit WorkerRegistred(patId, workerId);
    }

    function exit(bytes32 patId) external {
        PAT storage pat = _patById[patId];

        // check owner
        address owner = pat.owner;
        require(owner == msg.sender, "PAT doesn't exist");

        // change pat count
        uint256 newPatCount = _patCount;
        _ownersInfo[owner].patCount--;
        _patCount = --newPatCount;

        // load modules
        ICore core = _core();
        IConfigModule config = core.configModule();
        IStatusModule statusController = core.statusModule();

        // change status
        if (statusController.status() == DealStatus.Working && newPatCount < config.minWorkers()) {
            statusController.changeStatus(DealStatus.WaitingForWorkers);
        }

        // return collateral and index
        _freeIndexes.push(bytes32(pat.index));
        _withdrawRequests[owner].push(pat.collateral);

        // remove PAT
        delete _patById[patId];
        _patsIdsList.remove(patId);

        emit PATRemoved(patId);
    }

    function withdrawCollateral(address owner) external {
        IGlobalConfig globalConfig = _core().configModule().globalConfig();

        uint256 amount = _withdrawRequests[owner].confirmBy(block.timestamp - globalConfig.withdrawTimeout());
        globalConfig.fluenceToken().safeTransfer(owner, amount);

        emit CollateralWithdrawn(owner, amount);
    }
}

contracts/global/DealFactory.sol.content:
// SPDX-License-Identifier: Apache-2.0

pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts/proxy/ERC1967/ERC1967Proxy.sol";
import "../global/interfaces/IGlobalConfig.sol";
import "../deal/interfaces/ICore.sol";
import "../deal/interfaces/IConfigModule.sol";
import "../deal/interfaces/IPaymentModule.sol";
import "../deal/interfaces/IStatusModule.sol";
import "../deal/interfaces/IWorkersModule.sol";
import "./interfaces/IFactory.sol";
import "../deal/base/ModuleProxy.sol";

abstract contract DealFactoryState is IFactory {
    // ----------------- Types -----------------
    struct Deal {
        ICore core;
        IConfigModule configModule;
        IPaymentModule paymentModule;
        IStatusModule statusModule;
        IWorkersModule workersModule;
    }

    // ----------------- Events -----------------
    event DealCreated(
        Deal deal,
        address paymentToken,
        uint256 pricePerEpoch,
        uint256 requiredCollateral,
        uint256 minWorkers,
        uint256 targetWorkers,
        CIDV1 appCID,
        CIDV1[] effectorWasmsCids,
        uint256 epoch
    );

    // ----------------- Constants -----------------
    uint256 public constant PRICE_PER_EPOCH = 83 * 10 ** 15;
    uint256 public constant REQUIRED_COLLATERAL = 1 * 10 ** 18;

    // ----------------- Immutable -----------------
    IGlobalConfig public immutable globalConfig;
    IERC20 public immutable defaultPaymentToken;

    ICore public immutable coreImpl;
    IConfigModule public immutable configImpl;
    IPaymentModule public immutable paymentImpl;
    IStatusModule public immutable statusImpl;
    IWorkersModule public immutable workersImpl;

    // ----------------- Vars -----------------
    mapping(address => bool) public isDeal;

    constructor(
        IGlobalConfig globalConfig_,
        IERC20 defaultPaymentToken_,
        ICore coreImpl_,
        IConfigModule configImpl_,
        IPaymentModule paymentImpl_,
        IStatusModule statusImpl_,
        IWorkersModule workersImpl_
    ) {
        globalConfig = globalConfig_;
        defaultPaymentToken = defaultPaymentToken_;

        coreImpl = coreImpl_;
        configImpl = configImpl_;
        paymentImpl = paymentImpl_;
        statusImpl = statusImpl_;
        workersImpl = workersImpl_;
    }
}

contract DealFactory is DealFactoryState, UUPSUpgradeable {
    modifier onlyOwner() {
        require(msg.sender == globalConfig.owner(), "Only owner can call this function");
        _;
    }

    constructor(
        IGlobalConfig globalConfig_,
        IERC20 defaultPaymentToken_,
        ICore coreImpl_,
        IConfigModule configImpl_,
        IPaymentModule paymentImpl_,
        IStatusModule statusImpl_,
        IWorkersModule workersImpl_
    ) DealFactoryState(globalConfig_, defaultPaymentToken_, coreImpl_, configImpl_, paymentImpl_, statusImpl_, workersImpl_) {
        _disableInitializers();
    }

    // ----------------- Mutable -----------------
    function createDeal(
        uint256 minWorkers_,
        uint256 targetWorkers_,
        CIDV1 calldata appCID_,
        CIDV1[] calldata effectors
    ) external returns (address) {
        Deal memory deal;

        bytes memory emptyBytes;

        {
            // Create deal system
            ICore core = ICore(address(new ERC1967Proxy(address(coreImpl), emptyBytes)));
            IConfigModule configModule = IConfigModule(
                address(
                    new ModuleProxy(
                        address(configImpl),
                        abi.encodeWithSelector(
                            IConfigModule.initialize.selector,
                            defaultPaymentToken,
                            PRICE_PER_EPOCH,
                            REQUIRED_COLLATERAL,
                            appCID_,
                            minWorkers_,
                            targetWorkers_,
                            effectors
                        ),
                        address(core)
                    )
                )
            );

            IPaymentModule paymentModule = IPaymentModule(address(new ModuleProxy(address(paymentImpl), emptyBytes, address(core))));
            IStatusModule statusModule = IStatusModule(address(new ModuleProxy(address(statusImpl), emptyBytes, address(core))));
            IWorkersModule workersModule = IWorkersModule(address(new ModuleProxy(address(workersImpl), emptyBytes, address(core))));

            // Initialize deal system
            core.initialize(configModule, paymentModule, statusModule, workersModule);
            core.transferOwnership(msg.sender);

            // throw event
            deal.core = core;
            deal.configModule = configModule;
            deal.paymentModule = paymentModule;
            deal.statusModule = statusModule;
            deal.workersModule = workersModule;
        }

        isDeal[address(deal.core)] = true;

        emit DealCreated(
            deal,
            address(defaultPaymentToken),
            PRICE_PER_EPOCH,
            REQUIRED_COLLATERAL,
            minWorkers_,
            targetWorkers_,
            appCID_,
            effectors,
            deal.configModule.globalConfig().epochManager().currentEpoch()
        );

        return address(deal.core);
    }

    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}
}

